INSERT INTO item ( c_id ,  t_id ,  name ,  img ,  description ,  t_detail,  b_id) VALUES (1,5,"Moon Lence テント ワンタッチ 自動組立","img/tent05.png","〇インナーポケットテントの内部にはポケットが2ヵ所取り付けられており、小物を収容することができます。〇ペグテントを固定するためのペグとロープが付属しています。風の強さなど、状況に応じてご使用ください。〇両面ジッパー外側と内側の両サイドから開閉ができる、両面ジッパーを採用しています。","サイズ : 収納時:約93cm 展開時:幅280×高さ130×奥行き200cm 重量：3kg主素材 : フライシート----190Tポリエステル、グランドシート----210Dオックスフォード布+防水PU 耐水圧：2000ｍｍ テントポール：グラスファイバー付属品：ペグ*12 ロープ*8 自在金具*8 収納袋*1特徴：収束ベッドを解けて、自動に展開します。設営手間はかかりません。窓にメッシュ素材を採用したことにより通気性は抜群で、快適な空間を体感できます。虫よけとしても効果あります。",1);
