INSERT INTO item ( c_id ,  t_id ,  name ,  img ,  description ,  t_detail,  b_id) VALUES (1,10,"snow peak(スノーピーク) テント ソル Pro TP-700","img/tent10.png","大型サークル形状のソル Pro.なら、大人数でのコミュニケーションも円滑にとる事ができ、食を中心としたキャンプパーティを大いに盛り上げてくれます。 全てのパネルをフルメッシュにすれば、開放感のある空間となり、大自然を満喫しながらゆったりと過ごす事ができます。 広々開放空間で、手巻き寿司パーティや鍋パーティなど、大勢で楽しむキャンプ料理を存分に楽しんでください。","本体、シールドルーフ、リッジポール（×1）、Aフレーム（×3）、Cフレーム（×3）アップライトポール190㎝（×2）、ジュラピンペグ（×34）、自在付ロープ（3.5ｍ×6、2.5m×6、7m二又×2）、シームグリップ剤、ペグケース、フレームケース、キャリーバッグ○ キャリーバッグサイズ：40×80×40（h）cm ○ フレームケースサイズ：20×70×20（h）cm25.5kg",1);
