INSERT INTO item ( c_id ,  t_id ,  name ,  img ,  description ,  t_detail,  b_id) VALUES (2,2,"Sumolux (サモルックス)インナーシュラフ 寝袋","img/sleepingbag02.png","【生地】フリース生地の寝袋です。冬は寝袋のインナーとして、春夏は単品で使えます。寝袋の中にインナーとして入れると、保温性を向上させサラサラ快適に眠ることができる多用途】チャックは全開にすることができますので、登山、キャンプ、昼寝、車中泊の時に、インナーシュラフ、寝袋以外に、いろいろな使い方があり、ひざ掛け、毛布としてもご利用いただけます。※洗い可能:この薄いインナーシュラフが洗濯機で丸ごと洗いでもOK。","価格: ￥ 2,280 【収納袋付き】コンパクトに収納できる収納袋付き　収納サイズ：約高さ30cm×直径16cm【軽量】超軽量、重量わずか0.64kgです。コンパクト収納できて、簡単に持ち運び！サイズ】展開サイズ：長さ180cm×幅75cm、完全展開サイズ：長さ180cm×幅150cm",2);
INSERT INTO item ( c_id ,  t_id ,  name ,  img ,  description ,  t_detail,  b_id) VALUES (2,3,"BSTEN 寝袋 冬用 シュラフ ","img/sleepingbag03.png","あなたは極端な気象条件でも快適な睡眠を提供する高品質の寝袋をお探しですか？BSTENは快適性、携帯性、耐久性の間の完璧なバランスを提供する信じられないほどの寝袋デザインをあなたにもたらします。 私たちの寝袋の多くの素晴らしい機能と利点のいくつかは次のとおりです","【収納サイズ】25*25*45cm【製品重量】2650g/1900g【詰め物】ダウン【羽毛量】1800g/1200g【寝袋使用時】幅80*長さ210cm【広げた状態】幅160×長さ210cm【表面素材】380Tナイロン100％（防水性強）【肌面素材】320Tポリエステル【下限外気温度】-25℃(2.7kg)/-15℃(1.9kg）【快適外気温度】-10℃～10℃",2);
