INSERT INTO item ( c_id ,  t_id ,  name ,  img ,  description ,  t_detail,  b_id) VALUES (4,1,"ロゴス ランタン ブロンズランタン","img/lamp01.png","白灯油とホワイトガソリンは異なるものです。ホワイトガソリンの使用は危険ですので、専用の燃料をご使用ください。ご家庭の石油ストーブに使用する灯油ですと、精製度が低く、大量のススが発生しますので、専用の燃料をご使用ください。横風に強い","総重量:(約)700gv サイズ:(約)28cm 燃焼時間目安:(約)20時間 使用燃料:白灯油またはアロマランプオイル(1回の注油量:約360ml)",5);
